import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { role: routeRole } = useParams(); // optional if you're passing role in the URL
  const navigate = useNavigate();

  // ‚úÖ Fetch user role and normalize it
  const getUserRole = async (userId) => {
    console.log("üîç Fetching role for userId:", userId);

    const { data, error } = await supabase
      .from('user_profiles')
      .select('role')
      .eq('id', userId)
      .single();

    if (error) {
      console.error("‚ùå Error fetching user role:", JSON.stringify(error, null, 2));
      return null;
    }

    if (!data || !data.role) {
      console.warn("‚ö†Ô∏è No role found for userId:", userId);
      return null;
    }

    const normalizedRole = data.role.trim().toLowerCase(); // ‚úÖ normalize
    console.log("‚úÖ User role fetched:", normalizedRole);
    return normalizedRole;
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    const { data: authData, error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    if (error) {
      alert("Login failed: " + error.message);
      return;
    }

    const userId = authData.user.id;
    console.log("üÜî Logged-in user ID:", userId);

    const role = await getUserRole(userId);

    if (!role) {
      alert("Unable to fetch user role");
      return;
    }

    alert("‚úÖ Logged in as: " + role);

    // ‚úÖ Redirect based on role
    if (role === 'authority') {
      navigate('/authority-dashboard');
    } else if (role === 'citizen') {
      navigate('/citizen-dashboard');
    } else {
      alert("‚ö†Ô∏è Unknown role: " + role);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>{routeRole === 'authority' ? 'Authority' : 'Citizen'} Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required /><br /><br />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required /><br /><br />
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
