import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate } from 'react-router-dom';

function SignupPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('citizen');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    const { data: signUpData, error } = await supabase.auth.signUp({
      email,
      password,
    });

    if (error) return alert(error.message);

    const user = signUpData.user || signUpData.session?.user;
    if (!user) {
      alert("User creation failed.");
  return;
}
    if (user) {
      const { error: insertError } = await supabase
        .from('user_profiles')
        .insert([{ id: user.id, role }]);

      if (insertError) {
        console.error('Error inserting role:', insertError.message);
        alert('Signup failed while saving role.');
        return;
      }

      alert('Signup successful! Please log in.');
      navigate('/');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Signup</h1>
      <form onSubmit={handleSignup}>
        <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)} required /><br /><br />
        <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} required /><br /><br />
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="citizen">Citizen</option>
          <option value="authority">Authority</option>
        </select><br /><br />
        <button type="submit">Sign up</button>
      </form>
    </div>
  );
}

export default SignupPage;
