import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

function SignupPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('citizen');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    // 1. Sign up user
    const { data, error } = await supabase.auth.signUp({
      email,
      password,
    });

    if (error) {
      alert('Signup failed: ' + error.message);
      console.error('Signup error:', error);
      return;
    }

    const user = data?.user;

    if (!user) {
      alert('Signup succeeded but no user object returned!');
      return;
    }

    // 2. Insert role into user_profiles table
    // Recommended version with session user ID
    const { data: sessionData } = await supabase.auth.getSession();
    const sessionUserId = sessionData?.session?.user?.id;

    const { error: profileError } = await supabase
  .from('user_profiles')
  .insert([{ id: sessionUserId, role }]);


    if (profileError) {
      alert('Error saving user role: ' + profileError.message);
      console.error('user_profiles insert error:', profileError);
      return;
    }

    // 3. Insert log into auth_logs table
    const { error: logError, data: logData } = await supabase
      .from('auth_logs')
      .insert([
        {
          user_id: user.id,
          email: email,
          role: role,
          action: 'signup',
        },
      ]);

    if (logError) {
      console.error('auth_logs insert error:', logError);
    } else {
      console.log('Signup log insert response:', logData);
    }

    alert('Signup successful! Please check your email to confirm.');
    navigate('/login?role=' + role);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Signup</h2>
      <form onSubmit={handleSignup}>
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="citizen">Citizen</option>
          <option value="authority">Authority</option>
        </select><br /><br />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        /><br /><br />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        /><br /><br />
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
}

export default SignupPage;
