import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate } from 'react-router-dom';

function SignupPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('citizen');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    const { data: signUpData, error: signUpError } = await supabase.auth.signUp({
      email,
      password,
    });

    if (signUpError) {
      alert("Signup failed: " + signUpError.message);
      return;
    }

    const user = signUpData?.user || signUpData?.session?.user;

    if (!user) {
      alert("⚠️ Could not retrieve user ID after signup.");
      return;
    }

    // Insert role into user_profiles table
    const { error: insertError } = await supabase
      .from('user_profiles') // Make sure this table exists
      .insert([{ id: user.id, role }]);

    if (insertError) {
      console.error('❌ Error saving user role:', insertError.message);
      alert('Signup failed while saving role.');
      return;
    }

    alert('✅ Signup successful! Please log in.');
    navigate('/');
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Signup</h1>
      <form onSubmit={handleSignup}>
        <input
          type="email"
          placeholder="Email"
          onChange={(e) => setEmail(e.target.value)}
          required
        /><br /><br />
        <input
          type="password"
          placeholder="Password"
          onChange={(e) => setPassword(e.target.value)}
          required
        /><br /><br />
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="citizen">Citizen</option>
          <option value="authority">Authority</option>
        </select><br /><br />
        <button type="submit">Sign up</button>
      </form>
    </div>
  );
}

export default SignupPage;
