import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={styles.title}>Welcome to CivicFlow</h1>
        <p style={styles.subtitle}>
          Report civic issues. Stay informed. Empower your community.
        </p>

        <div style={styles.buttonContainer}>
          <Link to="/signup">
            <button style={styles.button}>Sign Up</button>
          </Link>
          <Link to="/login">
            <button style={styles.button}>Login</button>
          </Link>
        </div>
      </header>

      <section style={styles.section}>
        <h2>Key Features</h2>
        <ul style={styles.featureList}>
          <li>üìù Submit Complaints</li>
          <li>üìç View Complaints on Map</li>
          <li>üìä Authority Dashboard</li>
          <li>üîÑ Track Complaint Status</li>
        </ul>
      </section>

      <section style={styles.section}>
        <h2>How It Works</h2>
        <ol style={styles.howItWorks}>
          <li>Register/Login to your account</li>
          <li>Submit a complaint with location and evidence</li>
          <li>Track updates in real time</li>
        </ol>
      </section>

      <footer style={styles.footer}>
        <p>Made with ‚ù§Ô∏è for smart cities.</p>
      </footer>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    padding: '2rem',
    textAlign: 'center',
    backgroundColor: '#f7f9fb',
    minHeight: '100vh',
  },
  header: {
    marginBottom: '3rem',
  },
  title: {
    fontSize: '2.5rem',
    marginBottom: '1rem',
    color: '#333',
  },
  subtitle: {
    fontSize: '1.2rem',
    color: '#666',
  },
  buttonContainer: {
    marginTop: '1.5rem',
  },
  button: {
    padding: '0.8rem 1.5rem',
    margin: '0 0.5rem',
    fontSize: '1rem',
    border: 'none',
    borderRadius: '8px',
    backgroundColor: '#0077cc',
    color: 'white',
    cursor: 'pointer',
  },
  section: {
    margin: '2rem auto',
    maxWidth: '600px',
  },
  featureList: {
    listStyle: 'none',
    paddingLeft: 0,
    fontSize: '1.1rem',
    lineHeight: '2',
  },
  howItWorks: {
    textAlign: 'left',
    margin: '1rem auto',
    maxWidth: '400px',
    fontSize: '1rem',
    lineHeight: '1.8',
  },
  footer: {
    marginTop: '4rem',
    fontSize: '0.9rem',
    color: '#999',
  },
};

export default HomePage;
