import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate } from 'react-router-dom';

function SignupPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('citizen');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    // Step 1: Sign up user
    const { data: signUpData, error: signUpError } = await supabase.auth.signUp({
      email,
      password,
    });

    if (signUpError) {
      console.error("‚ùå Signup error:", signUpError.message);
      alert("Signup failed: " + signUpError.message);
      return;
    }

    const user = signUpData?.user;

    if (!user || !user.id) {
      console.error("‚ö†Ô∏è No user ID found in signup response:", signUpData);
      alert("Could not retrieve user ID after signup.");
      return;
    }

    // Step 2: Insert role into user_profiles table
    console.log("üì§ Inserting role:", role, "for user:", user.id);

    const { data: insertData, error: insertError } = await supabase
      .from('user_profiles')
      .insert([{ id: user.id, role }]);

    if (insertError) {
      console.error('‚ùå Error saving user role:', insertError.message);
      alert('Signup failed while saving role: ' + insertError.message);
      return;
    }

    console.log("‚úÖ Role inserted successfully:", insertData);
    alert('‚úÖ Signup successful! Please log in.');
    navigate('/');
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Signup</h1>
      <form onSubmit={handleSignup}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        /><br /><br />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        /><br /><br />
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="citizen">Citizen</option>
          <option value="authority">Authority</option>
        </select><br /><br />
        <button type="submit">Sign up</button>
      </form>
    </div>
  );
}

export default SignupPage;
