import React from 'react';
import { useNavigate } from 'react-router-dom';

function HomePage() {
const navigate = useNavigate();

return (
<div style={styles.container}>
<h1 style={styles.heading}>Welcome to CivicFlow</h1>
<div style={styles.cardContainer}>
<div style={styles.card} onClick={() => navigate('/login/citizen')}>
<h2>Citizen Login</h2>
<p>Submit complaints about civic issues</p>
</div>
<div style={styles.card} onClick={() => navigate('/login/authority')}>
<h2>Authority Login</h2>
<p>View and resolve submitted complaints</p>
</div>
</div>
</div>
);
}

const styles = {
container: { textAlign: 'center', padding: '2rem' },
heading: { fontSize: '2rem', marginBottom: '1rem' },
cardContainer: { display: 'flex', justifyContent: 'center', gap: '2rem' },
card: {
border: '1px solid #ccc',
borderRadius: '10px',
padding: '1.5rem',
width: '250px',
cursor: 'pointer',
backgroundColor: '#f9f9f9',
transition: '0.2s',
},
};

export default HomePage;
