// ✅ ComplaintForm.js
import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';

function ComplaintForm() {
  const [description, setDescription] = useState('');
  const [location, setLocation] = useState('');
  const [complaints, setComplaints] = useState([]);

  const fetchComplaints = async () => {
    const { data } = await supabase
      .from('complaints')
      .select('*')
      .eq('user_id', (await supabase.auth.getUser()).data.user.id);
    setComplaints(data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = (await supabase.auth.getUser()).data.user;
    await supabase.from('complaints').insert([{ user_id: user.id, issue_description: description, location, status: 'pending' }]);
    fetchComplaints();
    setDescription('');
    setLocation('');
  };

  useEffect(() => { fetchComplaints(); }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Submit Complaint</h2>
      <form onSubmit={handleSubmit}>
        <textarea placeholder="Issue Description" value={description} onChange={(e) => setDescription(e.target.value)} required /><br /><br />
        <input placeholder="Location" value={location} onChange={(e) => setLocation(e.target.value)} required /><br /><br />
        <button type="submit">Submit</button>
      </form>
      <h3>Your Complaints:</h3>
      <ul>
        {complaints.map(c => (
          <li key={c.id}>{c.issue_description} - {c.status}</li>
        ))}
      </ul>
    </div>
  );
}

export default ComplaintForm;
