import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { role: routeRole } = useParams(); // optional, if used in route
  const navigate = useNavigate();

  // Fetch user role from Supabase table
  const getUserRole = async (userId) => {
    console.log("ðŸ” Fetching role for userId:", userId);

    const { data, error } = await supabase
      .from('user_profiles')
      .select('role')
      .eq('id', userId) // ðŸ” Change to .eq('user_id', userId) if column is named `user_id`
      .single();

    if (error) {
      console.error("âŒ Error fetching user role:", JSON.stringify(error, null, 2));
      return null;
    }

    if (!data || !data.role) {
      console.warn("âš ï¸ No role found for userId:", userId);
      return null;
    }

    console.log("âœ… User role fetched:", data.role);
    return data.role;
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    const { data: authData, error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    if (error) {
      alert("Login failed: " + error.message);
      return;
    }

    const userId = authData.user.id;
    console.log("ðŸ†” Logged-in user ID:", userId);

    const role = await getUserRole(userId);

    if (!role) {
      alert("Unable to fetch user role");
      return;
    }

    alert("âœ… Logged in as: " + role);

    if (role === 'authority') {
      navigate('/authority-dashboard');
    } else {
      navigate('/citizen-dashboard');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>{routeRole === 'authority' ? 'Authority' : 'Citizen'} Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required /><br /><br />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required /><br /><br />
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
