// ✅ SignupPage.js
import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate, useSearchParams } from 'react-router-dom';

function SignupPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('citizen');
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  useEffect(() => {
    const roleParam = searchParams.get('role');
    if (roleParam) setRole(roleParam);
  }, [searchParams]);

  const handleSignup = async (e) => {
    e.preventDefault();

    const { data: signUpData, error: signUpError } = await supabase.auth.signUp({ email, password });
    if (signUpError) return alert("Signup failed: " + signUpError.message);

    const user = signUpData.user;
    const { error: insertError } = await supabase
      .from('user_profiles')
      .insert([{ id: user.id, role }]);

    if (insertError) return alert("Signup failed while saving role: " + insertError.message);

    alert('Signup successful! Please log in.');
    navigate('/login');
  };

  return (
    <form onSubmit={handleSignup} style={{ padding: '2rem' }}>
      <h2>Signup as {role}</h2>
      <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)} required /><br /><br />
      <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} required /><br /><br />
      <button type="submit">Sign up</button>
    </form>
  );
}

export default SignupPage;
