// ✅ LoginPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    const { data: authData, error } = await supabase.auth.signInWithPassword({ email, password });
    if (error) return alert("Login failed: " + error.message);

    const userId = authData.user.id;
    const { data: profile } = await supabase.from('user_profiles').select('role').eq('id', userId).single();

    if (!profile) return alert("Unable to fetch user role");
    const role = profile.role;

    if (role === 'authority') navigate('/authority-dashboard');
    else navigate('/submit-issue');
  };

  return (
    <form onSubmit={handleLogin} style={{ padding: '2rem' }}>
      <h2>Login</h2>
      <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)} required /><br /><br />
      <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} required /><br /><br />
      <button type="submit">Login</button>
    </form>
  );
}

export default LoginPage;
