import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const Dashboard = () => {
  const navigate = useNavigate();
  const [complaints, setComplaints] = useState([]);
  const [searchText, setSearchText] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      const { data, error } = await supabase
        .from('complaints')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) {
        console.error("Error loading complaints:", error);
      } else {
        setComplaints(data);
      }
    };

    fetchData();
  }, []);

  const filteredComplaints = complaints.filter((c) =>
    c.title.toLowerCase().includes(searchText.toLowerCase())
  );

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h1 style={{ fontSize: '2rem', marginBottom: '10px' }}>ðŸ“‹ Complaints Dashboard</h1>

      <button
        onClick={() => navigate('/submit-complaint')}
        style={{
          padding: '10px 20px',
          marginBottom: '20px',
          backgroundColor: '#007bff',
          color: '#fff',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer'
        }}
      >
        âž• Submit Complaint
      </button>

      <input
        type="text"
        placeholder="Search complaints"
        onChange={(e) => setSearchText(e.target.value)}
        style={{
          padding: '10px',
          marginBottom: '20px',
          width: '100%',
          maxWidth: '400px',
          display: 'block',
          border: '1px solid #ccc',
          borderRadius: '5px'
        }}
      />

      <div style={{ marginBottom: '20px' }}>
        <p><strong>Total Complaints:</strong> {complaints.length}</p>
        <p><strong>Filtered Results:</strong> {filteredComplaints.length}</p>
      </div>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
        gap: '16px'
      }}>
        {filteredComplaints.map((item) => (
          <div
            key={item.id}
            style={{
              background: '#f2f2f2',
              padding: '16px',
              borderRadius: '10px',
              boxShadow: '2px 2px 8px rgba(0,0,0,0.1)'
            }}
          >
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <p style={{ fontSize: '12px', color: '#555' }}>
              Submitted: {new Date(item.created_at).toLocaleString()}
            </p>
            {item.status && (
              <span style={{
                backgroundColor: item.status === 'Resolved' ? 'green' : 'orange',
                color: '#fff',
                padding: '4px 8px',
                borderRadius: '4px',
                fontSize: '12px'
              }}>
                {item.status}
              </span>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
