import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('citizen');
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  useEffect(() => {
    const roleParam = searchParams.get('role');
    if (roleParam) setRole(roleParam);
  }, [searchParams]);

  const handleLogin = async (e) => {
    e.preventDefault();

    const { data, error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    if (error) {
      alert('Login failed: ' + error.message);
      return;
    }

    const userId = data.user.id;
    console.log('Logged in user ID:', userId); // optional debug

    const { data: profile, error: profileError } = await supabase
      .from('user_profiles')
      .select('role')
      .eq('id', userId) // ✅ fixed here
      .single();

    if (profileError || !profile?.role) {
      console.error('Profile fetch error:', profileError);
      alert('Unable to fetch user role.');
      return;
    }

    console.log('User role:', profile.role); // optional debug

    if (profile.role === 'authority') {
      navigate('/authority-dashboard');
    } else {
      navigate('/submit-issue');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>{role === 'authority' ? 'Authority' : 'Citizen'} Login</h2>

      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        /><br /><br />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        /><br /><br />
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
