import React from 'react';
import { PieChart, Pie, Cell, Tooltip, ResponsiveContainer } from 'recharts';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

const sampleData = [
  { name: 'Road', value: 10 },
  { name: 'Electricity', value: 6 },
  { name: 'Water', value: 4 },
  { name: 'Other', value: 3 },
];

const sampleLocations = [
  { id: 1, lat: 17.385, lng: 78.4867, title: 'Complaint A' },
  { id: 2, lat: 17.44, lng: 78.48, title: 'Complaint B' },
];

const AuthorityDashboard = () => {
  return (
    <div style={{ padding: '2rem' }}>
      <h2>Authority Dashboard</h2>

      <div style={{ display: 'flex', gap: '2rem', flexWrap: 'wrap' }}>
        <div style={{ flex: 1 }}>
          <h3>Complaints by Type</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={sampleData}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={80}
                label
              >
                {sampleData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>

        <div style={{ flex: 1 }}>
          <h3>Complaints Map</h3>
          <MapContainer center={[17.385, 78.4867]} zoom={12} style={{ height: '300px', width: '100%' }}>
            <TileLayer
              attribution='&copy; OpenStreetMap contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {sampleLocations.map(loc => (
              <Marker key={loc.id} position={[loc.lat, loc.lng]}>
                <Popup>{loc.title}</Popup>
              </Marker>
            ))}
          </MapContainer>
        </div>
      </div>
    </div>
  );
};

export default AuthorityDashboard;
