import React from 'react';
import { Link } from 'react-router-dom';

function HomePage() {
  return (
    <div style={{ padding: '2rem', fontFamily: 'sans-serif', textAlign: 'center' }}>
      <h1>Welcome to CivicFlow Portal</h1>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '4rem', marginTop: '3rem' }}>
        
        {/* Citizen Section */}
        <div style={{ border: '1px solid #ccc', padding: '2rem', borderRadius: '8px', width: '250px' }}>
          <h2>Citizen</h2>
          <Link to="/signup?role=citizen">
            <button style={{ margin: '1rem' }}>Signup</button>
          </Link><br />
          <Link to="/login?role=citizen">
            <button>Login</button>
          </Link>
        </div>

        {/* Authority Section */}
        <div style={{ border: '1px solid #ccc', padding: '2rem', borderRadius: '8px', width: '250px' }}>
          <h2>Authority</h2>
          <Link to="/signup?role=authority">
            <button style={{ margin: '1rem' }}>Signup</button>
          </Link><br />
          <Link to="/login?role=authority">
            <button>Login</button>
          </Link>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
