import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';

// ✅ E.164 phone number validation function
const isValidE164 = (phone) => {
  return /^\+\d{10,15}$/.test(phone); // Must start with '+' and be 10-15 digits
};

function LoginPage() {
  const [emailOrPhone, setEmailOrPhone] = useState('');
  const [password, setPassword] = useState('');
  const [otp, setOtp] = useState('');
  const [loginMethod, setLoginMethod] = useState(null); // 'phone' or 'email'
  const [otpSent, setOtpSent] = useState(false);
  const [role, setRole] = useState('citizen');
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  useEffect(() => {
    const roleParam = searchParams.get('role');
    if (roleParam) setRole(roleParam);
  }, [searchParams]);

  const handleIdentifyMethod = async () => {
    const key = role === 'citizen' && emailOrPhone.startsWith('+') ? 'phone' : 'email';

    const { data, error } = await supabase
      .from('user_profiles')
      .select('id, method, role')
      .limit(1)
      .eq(key, emailOrPhone)
      .maybeSingle();

    if (error || !data) {
      alert('User not found or error occurred.');
      return;
    }

    setLoginMethod(data.method || (role === 'authority' ? 'email' : 'phone'));

    if (data.method === 'phone') {
      // ✅ Check E.164 phone number format
      if (!isValidE164(emailOrPhone)) {
        alert("Invalid phone number format. Use E.164 format like +919876543210");
        return;
      }

      const { error: otpErr } = await supabase.auth.signInWithOtp({ phone: emailOrPhone });
      if (otpErr) {
        alert('Failed to send OTP: ' + otpErr.message);
      } else {
        alert('OTP sent!');
        setOtpSent(true);
      }
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    if (role === 'citizen' && loginMethod === 'phone' && otpSent) {
      const { data, error } = await supabase.auth.verifyOtp({
        phone: emailOrPhone,
        token: otp,
        type: 'sms',
      });

      if (error) return alert('OTP verification failed: ' + error.message);

      navigate('/submit-issue');
      return;
    }

    if (role === 'authority' || (role === 'citizen' && loginMethod === 'email')) {
      const { data, error } = await supabase.auth.signInWithPassword({
        email: emailOrPhone,
        password,
      });

      if (error) return alert('Login failed: ' + error.message);

      const userId = data.user.id;
      const { data: profile, error: profileError } = await supabase
        .from('user_profiles')
        .select('role')
        .eq('id', userId)
        .single();

      if (profileError || !profile?.role) {
        alert('Unable to fetch user role.');
        return;
      }

      if (profile.role === 'authority') {
        navigate('/authority-dashboard');
      } else {
        navigate('/submit-issue');
      }
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>{role === 'authority' ? 'Authority' : 'Citizen'} Login</h2>

      <form onSubmit={handleLogin}>
        <input
          type={role === 'citizen' ? 'text' : 'email'}
          placeholder={role === 'citizen' ? 'Phone (+91...) or Email' : 'Email'}
          value={emailOrPhone}
          onChange={(e) => setEmailOrPhone(e.target.value)}
          required
        /><br /><br />

        {role === 'citizen' && !loginMethod && (
          <button type="button" onClick={handleIdentifyMethod}>
            Continue
          </button>
        )}

        {(role === 'authority' || loginMethod === 'email') && (
          <>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            /><br /><br />
            <button type="submit">Login</button>
          </>
        )}

        {role === 'citizen' && loginMethod === 'phone' && otpSent && (
          <>
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              required
            /><br /><br />
            <button type="submit">Verify OTP</button>
          </>
        )}
      </form>
    </div>
  );
}

export default LoginPage;
