import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate, useSearchParams } from 'react-router-dom';

function SignupPage() {
  const [role, setRole] = useState('citizen');
  const [method, setMethod] = useState('email'); // citizen: phone/email
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [phone, setPhone] = useState('');
  const [otp, setOtp] = useState('');
  const [otpSent, setOtpSent] = useState(false);
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  useEffect(() => {
    const roleParam = searchParams.get('role');
    if (roleParam) setRole(roleParam);
  }, [searchParams]);

  const handleCitizenSignup = async (e) => {
    e.preventDefault();

    if (method === 'phone') {
      const { error } = await supabase.auth.signInWithOtp({ phone });
      if (error) {
        alert('OTP failed: ' + error.message);
      } else {
        alert('OTP sent!');
        setOtpSent(true);
      }
    } else {
      const { data, error } = await supabase.auth.signUp({ email, password });
      if (error) return alert('Signup failed: ' + error.message);
      await supabase.from('user_profiles').insert([{ id: data.user.id, role, method: 'email' }]);
      alert('Signup successful!');
      navigate('/login?role=citizen');
    }
  };

  const handleCitizenVerifyOtp = async (e) => {
    e.preventDefault();
    const { data, error } = await supabase.auth.verifyOtp({ phone, token: otp, type: 'sms' });
    if (error) return alert('OTP verification failed: ' + error.message);

    await supabase.from('user_profiles').insert([{ id: data.user.id, role, method: 'phone' }]);
    alert('Signup complete!');
    navigate('/submit-issue');
  };

  const handleAuthoritySignup = async (e) => {
    e.preventDefault();
    const { data, error } = await supabase.auth.signUp({ email, password });
    if (error) return alert('Signup failed: ' + error.message);
    await supabase.from('user_profiles').insert([{ id: data.user.id, role }]);
    alert('Signup successful!');
    navigate('/login?role=authority');
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>{role === 'authority' ? 'Authority' : 'Citizen'} Signup</h2>

      {role === 'citizen' && (
        <div>
          <label>Signup method:</label><br />
          <button onClick={() => setMethod('email')}>Email</button>
          <button onClick={() => setMethod('phone')}>Phone</button><br /><br />
        </div>
      )}

      <form onSubmit={
        role === 'authority'
          ? handleAuthoritySignup
          : otpSent && method === 'phone'
          ? handleCitizenVerifyOtp
          : handleCitizenSignup
      }>
        {role === 'citizen' && method === 'phone' && (
          <>
            <input
              type="tel"
              placeholder="Phone (+91...)"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              required
            /><br /><br />
            {otpSent && (
              <>
                <input
                  type="text"
                  placeholder="Enter OTP"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                  required
                /><br /><br />
              </>
            )}
          </>
        )}

        {((role === 'authority') || (role === 'citizen' && method === 'email')) && (
          <>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            /><br /><br />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            /><br /><br />
          </>
        )}

        <button type="submit">{otpSent ? 'Verify OTP' : 'Sign Up'}</button>
      </form>
    </div>
  );
}

export default SignupPage;
