import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate } from 'react-router-dom';

function SignupPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('citizen');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    // Step 1: Sign up
    const { data: signUpData, error: signUpError } = await supabase.auth.signUp({
      email,
      password,
    });

    if (signUpError) {
      alert("Signup failed: " + signUpError.message);
      return;
    }

    const user = signUpData.user;
    console.log("üÜî User ID:", user?.id);

    // ‚úÖ Wait for session to be established
    let session = signUpData.session;

    // If session is not available immediately, fetch it manually
    if (!session) {
      const {
        data: { session: fetchedSession },
        error: sessionError
      } = await supabase.auth.getSession();

      if (sessionError) {
        alert("Signup session failed: " + sessionError.message);
        return;
      }

      session = fetchedSession;
    }

    if (!session || !session.user?.id) {
      alert("Could not get session to insert role.");
      return;
    }

    const userId = session.user.id;
    console.log("üì§ Inserting role:", role, "for user:", userId);

    // Step 2: Insert role
    const { data: insertData, error: insertError } = await supabase
      .from('user_profiles')
      .insert([{ id: userId, role }]);

    if (insertError) {
      console.error("‚ùå Error saving user role:", insertError.message);
      alert('Signup failed while saving role: ' + insertError.message);
      return;
    }

    console.log("‚úÖ Role inserted:", insertData);
    alert('‚úÖ Signup successful! Please log in.');
    navigate('/');
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Signup</h1>
      <form onSubmit={handleSignup}>
        <input
          type="email"
          placeholder="Email"
          onChange={(e) => setEmail(e.target.value)}
          required
        /><br /><br />
        <input
          type="password"
          placeholder="Password"
          onChange={(e) => setPassword(e.target.value)}
          required
        /><br /><br />
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="citizen">Citizen</option>
          <option value="authority">Authority</option>
        </select><br /><br />
        <button type="submit">Sign up</button>
      </form>
    </div>
  );
}

export default SignupPage;
